import avaTest, { ExecutionContext, TestFn } from 'ava';
import { TestContext } from '../../mock/interface';
import { characterSet } from '../base';
import { findCharacterSet, isASCII } from './characterSet';

const test = avaTest as TestFn<TestContext>;

const getLabel = (name: string) => name.substring(0, name.indexOf('.'));

test('should retrieve letter character set for nick.fil', (t: ExecutionContext<TestContext>) => {
  const FNS = 'nick.fil';
  const result = findCharacterSet(getLabel(FNS));
  t.is(result, characterSet.LETTER);
});

test('should retrieve digit character set for 9898.fil', (t: ExecutionContext<TestContext>) => {
  const FNS = '9898.fil';
  const result = findCharacterSet(getLabel(FNS));
  t.is(result, characterSet.DIGIT);
});

test('should retrieve alphanumeric character set for 0xmatoken.fil', (t: ExecutionContext<TestContext>) => {
  const FNS = '0xmatoken.fil';
  const result = findCharacterSet(getLabel(FNS));
  t.is(result, characterSet.ALPHANUMERIC);
});

